$$ = Dom7;
PAGE_SIZE = 20;
db = new loki('words.db', {
    autoload: true,
    autoloadCallback: databaseInitialize,
    autosave: true,
    autosaveInterval: 4000
});
words = null;
loading = false;
function databaseInitialize() {
    words = db.getCollection("words");
    if (words === null) {
        words = db.addCollection("words");
        var list = getInitialList();
        for (var i = 0; i < list.length; i++) {
            words.insert(list[i]);
        }
    }
}
function loadInitialItems() {
    $('#wordList').html("");
    if (!$("#btnSwitch")[0].checked) {
        $("#wordList").css("direction", "ltr");
    }
    else {
        $("#wordList").css("direction", "rtl");
    }
    var items = getNotAnalyzedList(0, PAGE_SIZE);
    for (var i = 0; i < items.length; i++) {
        $('#wordList').append(getListItem(items[i], false, false));
    }
}
function loadBaladItems() {
    $('#baladWordList').html("");
    if (!$("#btnSwitch")[0].checked) {
        $("#baladWordList").css("direction", "ltr");
    }
    else {
        $("#baladWordList").css("direction", "rtl");
    }
    var items = getBaladList(0, PAGE_SIZE);
    for (var i = 0; i < items.length; i++) {
        $('#baladWordList').append(getListItem(items[i], true, false));
    }
}
function loadNaBaladItems() {
    $('#nabaladWordList').html("");
    if (!$("#btnSwitch")[0].checked) {
        $("#nabaladWordList").css("direction", "ltr");
    }
    else {
        $("#nabaladWordList").css("direction", "rtl");
    }
    var items = getNaBaladList(0, PAGE_SIZE);
    for (var i = 0; i < items.length; i++) {
        $('#nabaladWordList').append(getListItem(items[i], false, true));
    }
}

function getNotAnalyzedList(offset, size) {
    if (!$("#btnSwitch")[0].checked) {
        return words.chain().find({ enbalad: undefined, ennabalad: undefined }).offset(offset).limit(size).data();
    }
    else {
        return words.chain().find({ fabalad: undefined, fanabalad: undefined }).offset(offset).limit(size).data();
    }

}

function getBaladList(offset, size) {
    if (!$("#btnSwitch")[0].checked) {
        return words.chain().find({ enbalad: true }).offset(offset).limit(size).data();
    }
    else {
        return words.chain().find({ fabalad: true }).offset(offset).limit(size).data();
    }
}
function getNaBaladList(offset, size) {
    if (!$("#btnSwitch")[0].checked) {
        return words.chain().find({ ennabalad: true }).offset(offset).limit(size).data();
    }
    else {
        return words.chain().find({ fanabalad: true }).offset(offset).limit(size).data();
    }
}
function markBalad(wordId) {
    var mimir = words.findOne({ id: wordId });
    if (!$("#btnSwitch")[0].checked) {
        mimir.enbalad = true;
        mimir.ennabalad = undefined;
    }
    else {
        mimir.fabalad = true;
        mimir.fanabalad = undefined;
    }
    words.update(mimir);
}

function markNaBalad(wordId) {
    var mimir = words.findOne({ id: wordId });
    if (!$("#btnSwitch")[0].checked) {
        mimir.ennabalad = true;
        mimir.enbalad = undefined;
    }
    else {
        mimir.fanabalad = true;
        mimir.fabalad = undefined;
    }
    words.update(mimir);
}

function getListItem(obj, isBalad, isNaBalad) {
    var li = $("<li></li>");
    li.addClass("item-content");
    var div = $("<div></div>");
    div.addClass("item-inner");

    var div1 = $("<div></div>");
    div1.addClass("item-title");
    div1.addClass("button");
    div1.css("min-width", "80px");

    var key = obj.eng;
    var value = obj.per;
    if (!$("#btnSwitch")[0].checked) {
        div1.html(key);
        div1.data("trans", value);
    }
    else {
        div1.html(value);
        div1.data("trans", key);
    }
    div1.click(function () {
        var trans = $(this).data().trans;
        myApp.alert("", trans);
    });

    var after = $("<div></div>");
    after.addClass("item-after");

    var balad = $("<a/>");
    balad.addClass("button");
    balad.addClass("color-green");
    balad.addClass("baladButton");
    balad.html("میدونم");
    balad.data("id", obj.id);
    balad.click(function () {
        markBalad($(this).data().id);
        $(this).parent().parent().parent().remove();
    });

    var nabalad = $("<a/>");
    nabalad.addClass("button");
    nabalad.addClass("color-red");
    nabalad.addClass("baladButton");
    nabalad.html("نمیدونم");
    nabalad.data("id", obj.id);
    nabalad.click(function () {
        markNaBalad($(this).data().id);
        $(this).parent().parent().parent().remove();
    });

    if (!isBalad && !isNaBalad) {
        after.append(balad);
        after.append(nabalad);
    }
    else if (isNaBalad) {
        var yad = $("<a/>");
        yad.addClass("button");
        yad.addClass("color-green");
        yad.addClass("baladButton");
        yad.html("میدونم");
        yad.data("id", obj.id);
        yad.click(function () {
            markBalad($(this).data().id);
            $(this).parent().parent().parent().remove();
        });
        after.append(yad);
    } else if (isBalad) {
        var yad = $("<a/>");
        yad.addClass("button");
        yad.addClass("color-red");
        yad.addClass("baladButton");
        yad.html("نمیدونم");
        yad.data("id", obj.id);
        yad.click(function () {
            markNaBalad($(this).data().id);
            $(this).parent().parent().parent().remove();
        });
        after.append(yad);
    }
    div.append(div1);
    div.append(after);
    li.append(div);
    return li;
}


