function markBalad(wordId, isWord) {
    var mimir;
    if (isWord) {
        mimir = words.findOne({ id: wordId });
    }
    else {
        mimir = sentences.findOne({ id: wordId });
    }
    if (!$("#btnSwitch")[0].checked) {
        mimir.enbalad = true;
        mimir.ennabalad = undefined;
    }
    else {
        mimir.fabalad = true;
        mimir.fanabalad = undefined;
    }
    if (isWord) {
        words.update(mimir);
    }
    else {
        sentences.update(mimir);
    }
}

function markNaBalad(wordId, isWord) {
    var mimir;
    if (isWord) {
        mimir = words.findOne({ id: wordId });
    }
    else {
        mimir = sentences.findOne({ id: wordId });
    }
    if (!$("#btnSwitch")[0].checked) {
        mimir.ennabalad = true;
        mimir.enbalad = undefined;
    }
    else {
        mimir.fanabalad = true;
        mimir.fabalad = undefined;
    }
    if (isWord) {
        words.update(mimir);
    }
    else {
        sentences.update(mimir);
    }
}

function refreshCounts() {
    var notAnalyzedCount = 0;
    var baladCount = 0;
    var nabaladCount = 0;
    var senNotAnalyzedCount = 0;
    var senBaladCount = 0;
    var senNabaladCount = 0;
    if (!$("#btnSwitch")[0].checked) {
        notAnalyzedCount = words.chain().find({ enbalad: undefined, ennabalad: undefined }).count();
        baladCount = words.chain().find({ enbalad: true }).count();
        nabaladCount = words.chain().find({ ennabalad: true }).count();
        senNotAnalyzedCount = sentences.chain().find({ enbalad: undefined, ennabalad: undefined }).count();
        senBaladCount = sentences.chain().find({ enbalad: true }).count();
        senNabaladCount = sentences.chain().find({ ennabalad: true }).count();

    }
    else {
        notAnalyzedCount = words.chain().find({ fabalad: undefined, fanabalad: undefined }).count();
        baladCount = words.chain().find({ fabalad: true }).count();
        nabaladCount = words.chain().find({ fanabalad: true }).count();
        senNotAnalyzedCount = sentences.chain().find({ fabalad: undefined, fanabalad: undefined }).count();
        senBaladCount = sentences.chain().find({ fabalad: true }).count();
        senNabaladCount = sentences.chain().find({ fanabalad: true }).count();
    }

    $("#btnNotAnalyzed").html("لغات بررسی نشده (" + notAnalyzedCount + ")");
    $("#btnNaBalad").html("لغاتی که بلد نیستم (" + nabaladCount + ")");
    $("#btnBalad").html("لغاتی که بلدم (" + baladCount + ")");
    $("#btnSenNotAnalyzed").html("جملات بررسی نشده (" + senNotAnalyzedCount + ")");
    $("#btnSenNaBalad").html("جملاتی که بلد نیستم (" + senNabaladCount + ")");
    $("#btnSenBalad").html("جملاتی که بلدم (" + senBaladCount + ")");
}

function getListItem(obj, isBalad, isNaBalad, isWord) {
    var li = $("<li></li>");
    li.addClass("item-content");
    var div = $("<div></div>");
    div.addClass("item-inner");

    var div1 = $("<div></div>");
    div1.addClass("item-title");
    div1.addClass("button");
    div1.css("min-width", "80px");

    var key = obj.eng;
    var value = obj.per;
    if (!$("#btnSwitch")[0].checked) {
        div1.html(key);
        div1.data("trans", value);
    }
    else {
        div1.html(value);
        div1.data("trans", key);
    }
    div1.click(function () {
        var trans = $(this).data().trans;
        myApp.alert("", trans);
    });

    var after = $("<div></div>");
    after.addClass("item-after");

    var balad = $("<a/>");
    balad.addClass("button");
    balad.addClass("color-green");
    balad.addClass("baladButton");
    balad.html("میدونم");
    balad.data("id", obj.id);
    balad.click(function () {
        markBalad($(this).data().id, isWord);
        $(this).parent().parent().parent().remove();
    });

    var nabalad = $("<a/>");
    nabalad.addClass("button");
    nabalad.addClass("color-red");
    nabalad.addClass("baladButton");
    nabalad.html("نمیدونم");
    nabalad.data("id", obj.id);
    nabalad.click(function () {
        markNaBalad($(this).data().id,isWord);
        $(this).parent().parent().parent().remove();
    });

    if (!isBalad && !isNaBalad) {
        after.append(balad);
        after.append(nabalad);
    }
    else if (isNaBalad) {
        var yad = $("<a/>");
        yad.addClass("button");
        yad.addClass("color-green");
        yad.addClass("baladButton");
        yad.html("میدونم");
        yad.data("id", obj.id);
        yad.click(function () {
            markBalad($(this).data().id, isWord);
            $(this).parent().parent().parent().remove();
        });
        after.append(yad);
    } else if (isBalad) {
        var yad = $("<a/>");
        yad.addClass("button");
        yad.addClass("color-red");
        yad.addClass("baladButton");
        yad.html("نمیدونم");
        yad.data("id", obj.id);
        yad.click(function () {
            markNaBalad($(this).data().id,isWord);
            $(this).parent().parent().parent().remove();
        });
        after.append(yad);
    }
    div.append(div1);
    div.append(after);
    li.append(div);
    return li;
}

